import React, { useContext, useState } from 'react';
import { View , Text , TextInput ,StyleSheet ,ScrollView ,Button , TouchableOpacity  } from 'react-native';
import DropDown from '../../components/dropdown/dropdown';
import * as ImagePicker from 'expo-image-picker';
import ImagePickerExample from '../../components/image-picker/image-picker';
import CnicPick1 from '../../components/cnic/cnic-pic1';
import CnicPick2 from '../../components/cnic/cnic-pic2';
import { GlobalContext } from '../../context/context';


const MainHome = () => {
    let [name , setName] = useState('');
    let [fatherName , setFatherName] = useState('');
    let [cnic , setCnic] = useState('');
    let [dateOfBirth , setDateOfBirth] = useState('');
    let [familyMembers , setFamilyMembers] = useState('');
    let [monthlyIncome , setMonthlyIncome] = useState('');
    let [MonthlyRation , setMonthlyRation] = useState('');

    let {state , dispatch} = useContext(GlobalContext)
    const submitFormFunc = () =>{
        console.log(state)
    }

    return (
        <ScrollView>
        <View>
            <View>
                <TextInput onChangeText={(e)=>{setName(e)}} style={styles.input} placeholder="Enter name" value={name} />
            </View>
            <View>
                <TextInput value={fatherName} onChangeText={(e)=>{setFatherName(e)}} style={styles.input} placeholder="Enter father name" />
            </View>
            <View>
                <TextInput style={styles.input} placeholder="Enter cnic number" />
            </View>
            <View>
                <TextInput style={styles.input} placeholder="Enter Date of Birth" />
            </View>
            <View>
                <TextInput style={styles.input} placeholder="Enter family members" />
            </View> 
            <View>
                <TextInput style={styles.input} placeholder="Enter Monthy Income" />
            </View>
            <View>
                <TextInput style={styles.input} placeholder="Enter Monthy ration" />
            </View>
            <View>
                <ImagePickerExample />
            </View>
            <View>
                <CnicPick1 />
            </View>
            <View>
                <CnicPick2 />
            </View>
            <View>
                <TouchableOpacity onPress={submitFormFunc}><Text>Submit Form</Text></TouchableOpacity>
            </View>

    </View>
    </ScrollView>
    )
}

const styles = StyleSheet.create({

    input: {
        height: 40,
        margin: 12,
        borderWidth: 1,
        borderColor : 'rgb(195, 195, 195)',
        padding: 10,
        borderRadius : '5px',

      //   backgroundColor : '#ffffff'
      },

})

export default MainHome;
