import React, { useContext, useState } from 'react';
import { View , Text , StyleSheet ,TextInput , TouchableOpacity} from 'react-native';


const MainLogin = () => {
    let [email , setEmail] = useState('');
    let [password , setPassword] = useState('');
    function loginFunc(){}
    return (
        <View>
            <View>
                <TextInput style={styles.input} value={email} onChange={(e)=>{setEmail(e)}}  />
            </View>
            <View>
                <TextInput value={password} onChange={(e)=>{setPassword(e)}}  />
            </View>
            <View>
                <TouchableOpacity onPress={loginFunc}><Text>Log in</Text></TouchableOpacity>
            </View>
        </View>
    )
}


const styles = StyleSheet.create({
    input: {
      height: 40,
      margin: 12,
      borderWidth: 1,
      padding: 10,
    },
  });

export default MainLogin;
