import * as React from 'react';
import { View ,Text} from 'react-native';
import { RadioButton } from 'react-native-paper';

const MyComponent = () => {
  const [checked, setChecked] = React.useState('first');
  function radioValue(value){
      console.log(value)
  }

  return (
    <View style={{flex:1, justifyContent:'center', alignItems:'center' , width:'100%' , flexDirection:'column'}}>
     <View>
     <RadioButton
        value="first"
        status={ checked === 'first' ? 'checked' : 'unchecked' }
        onPress={() => radioValue('first')}
      />
      <Text>Monthly Ration</Text>
     </View>
    <View>
    <RadioButton
        value="second"
        status={ checked === 'second' ? 'checked' : 'unchecked' }
        onPress={() => radioValue('second')}
      />
      <Text>Daily Ration 1</Text>
    </View>
     <View>
     <RadioButton
        value="second"
        status={ checked === 'second' ? 'checked' : 'unchecked' }
        onPress={() => radioValue('second')}
      />
      <Text>Daily Ration 2</Text>
     </View>
     <View>
     <RadioButton
        value="second"
        status={ checked === 'second' ? 'checked' : 'unchecked' }
        onPress={() => radioValue('second')}
      />
      <Text>Daily Ration 3</Text>
     </View>
      <View>
      <RadioButton
        value="second"
        status={ checked === 'second' ? 'checked' : 'unchecked' }
        onPress={() => radioValue('second')}
      />
      <Text>Daily Ration 4</Text>

      </View>
    </View>
  );
};

export default MyComponent;
