import React from 'react';
import { View , Text , TextInput ,StyleSheet ,ScrollView  } from 'react-native';
import DropDown from '../../components/dropdown/dropdown';
import {Picker} from '@react-native-picker/picker';

// You can import from local files
import DropDownPicker from 'react-native-dropdown-picker'

const MainHome = () => {
    const [selectedLanguage, setSelectedLanguage] = useState();

    return (
        <ScrollView>
        <View>
            <View>
                <TextInput style={styles.input} placeholder="Enter name" />
            </View>
            <View>
                <TextInput style={styles.input} placeholder="Enter father name" />
            </View>
            <View>
                <TextInput style={styles.input} placeholder="Enter cnic number" />
            </View>
            <View>
                <TextInput style={styles.input} placeholder="Enter Date of Birth" />
            </View>
            <View>
                <TextInput style={styles.input} placeholder="Enter family members" />
            </View> 
            <View>
                <TextInput style={styles.input} placeholder="Enter Monthy Income" />
            </View>

            <Picker
  selectedValue={selectedLanguage}
  onValueChange={(itemValue, itemIndex) =>
    setSelectedLanguage(itemValue)
  }>
  <Picker.Item label="Java" value="java" />
  <Picker.Item label="JavaScript" value="js" />
</Picker>


    </View>
    </ScrollView>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        paddingTop: Constants.statusBarHeight,
        backgroundColor: '#ecf0f1',
        padding: 8,
      },
      paragraph: {
        margin: 24,
        fontSize: 18,
        fontWeight: 'bold',
        textAlign: 'center',
      },

    input: {
        height: 40,
        margin: 12,
        borderWidth: 1,
        borderColor : 'rgb(195, 195, 195)',
        padding: 10,
        borderRadius : '5px',

      //   backgroundColor : '#ffffff'
      },

})

export default MainHome;
