import React, { useContext, useState } from 'react';
import { View , Text , StyleSheet ,TextInput , TouchableOpacity} from 'react-native';


const MainLogin = () => {
    let [email , setEmail] = useState('');
    let [password , setPassword] = useState('');
    function loginFunc(){}
    return (
        <View style={styles.container}>
            <View>
                <TextInput style={styles.input} value={email} onChange={(e)=>{setEmail(e)}}  />
            </View>
            <View>
                <TextInput style={styles.input} value={password} onChange={(e)=>{setPassword(e)}}  />
            </View>
            <View style={styles.btnDiv}>
                <TouchableOpacity style={styles.touchableBtn} onPress={loginFunc}><Text >Log in</Text></TouchableOpacity>
            </View>
        </View>
    )
}


const styles = StyleSheet.create({
    input: {
      height: 40,
      margin: 12,
      borderWidth: 1,
      padding: 10,
      borderRadius : '5px',
    //   backgroundColor : '#ffffff'
    },
    container :{
        flex:1,
        justifyContent : "center",
        alignItems : 'center',
        // backgroundColor: '#000000',
        width : '100%',
        height : '100%'
    },
    btnDiv : {
        backgroundColor : 'violet',
    },
    touchableBtn: {
        // height: '3rem',
        // width : '10rem'
        paddingTop: 10,
        paddingBottom : 10,
        paddingLeft : 10,
        paddingRight: 10
    }
  });

export default MainLogin;
